(108, 'needs-update-eway-payment-gateway-rin-2022-12-20', 'update', 'en_US', 'Security vulnerability patched in WooCommerce Eway Gateway', 'In response to a potential vulnerability identified in WooCommerce Eway Gateway versions 3.1.0 to 3.5.0, we’ve worked to deploy security fixes and have released an updated version.\r\nNo external exploits have been detected, but we recommend you update to your latest supported version 3.1.26, 3.2.3, 3.3.1, 3.4.6, or 3.5.1', '{}', 'pending', 'woocommerce.com', '2023-01-05 10:01:37', NULL, 0, 'plain', '', 0, 'info'),
(109, 'updated-eway-payment-gateway-rin-2022-12-20', 'update', 'en_US', 'WooCommerce Eway Gateway has been automatically updated', 'Your store is now running the latest secure version of WooCommerce Eway Gateway. We worked with the WordPress Plugins team to deploy a software update to stores running WooCommerce Eway Gateway (versions 3.1.0 to 3.5.0) in response to a security vulnerability that was discovered.', '{}', 'pending', 'woocommerce.com', '2023-01-05 10:01:37', NULL, 0, 'plain', '', 0, 'info'),
(110, 'storeediting_feedback_jan_2023', 'marketing', 'en_US', 'Tell us what you need to customize your store!', 'We need your feedback. Please spare a few minutes to share your input on what is most important to you when designing and customizing your storefront to help us build the features you need.', '{}', 'unactioned', 'woocommerce.com', '2023-01-13 13:10:37', NULL, 0, 'plain', '', 0, 'info'),
(111, 'ecomm-wc-navigation-survey-2023', 'info', 'en_US', 'Navigating WooCommerce on WordPress.com', 'We are improving the WooCommerce navigation on WordPress.com and would love your help to make it better! Please share your experience with us in this 2-minute survey.', '{}', 'pending', 'woocommerce.com', '2023-01-16 10:07:18', NULL, 0, 'plain', '', 0, 'info'),
(112, 'affirm_q1_2023', 'marketing', 'en_US', 'Turn more browsers into buyers with flexible payments', 'When you offer flexible payment options to shoppers, they\'re more likely to convert. It’s why businesses offering Affirm see an average order value lift of 63%. Affirm tailors your store\'s payment options with terms proven to reach new customers, boost conversions, and grow sales.', '{}', 'pending', 'woocommerce.com', '2023-03-13 09:00:21', NULL, 0, 'plain', '', 0, 'info'),
(113, 'google_listings_ads_pmax_i1_q1_2023_no_gla', 'marketing', 'en_US', 'Create more engaging ads – without the hard work', 'Get in front of millions of shoppers searching for products like yours with Google Listings &amp; Ads. With new customization features, Google automatically tests multiple combinations of text and images to create the most engaging ad to boost your business. Plus, get up to $500 in ad credit – terms and conditions apply.', '{}', 'unactioned', 'woocommerce.com', '2023-03-16 11:43:51', NULL, 0, 'plain', '', 0, 'info'),
(114, 'google_listings_ads_pmax_i1_q1_2023_with_gla', 'marketing', 'en_US', 'New customization features to boost your business', 'You can now add custom images, messaging, and URLs to campaigns in Google Listings &amp; Ads. Google then automatically tests multiple combinations to create the most engaging version to help boost your business. Get more sales with dynamic content – edit an existing campaign or create a new ad now.', '{}', 'pending', 'woocommerce.com', '2023-03-16 11:43:51', NULL, 0, 'plain', '', 0, 'info'),
