(175, 'google_uxstudy_recruitment_q3-2023', 'marketing', 'en_US', 'Share your feedback in exchange for $150', 'Woo has teamed up with Google to collect feedback on advertising products and services available to you. If selected, you’ll receive an incentive equal to $150 USD for a 60-minute interview that can help improve the products you use to advertise. If interested, please take 2 minutes to complete the screening survey.', '{}', 'pending', 'woocommerce.com', '2023-08-10 09:54:33', NULL, 0, 'plain', '', 0, 'info'),
(176, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-08-23 09:46:19', NULL, 0, 'plain', '', 0, 'info'),
